import React, { useState } from 'react';
import '../Generator/Generator.css';

function Generator() {
    const [password, setPassword] = useState('');
    const [length, setLength] = useState(12);
    const [includeUppercase, setIncludeUppercase] = useState(true);
    const [includeLowercase, setIncludeLowercase] = useState(true);
    const [includeNumbers, setIncludeNumbers] = useState(true);
    const [includeSymbols, setIncludeSymbols] = useState(true);

    const generatePassword = () => {
        const uppercaseChars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ';
        const lowercaseChars = 'abcdefghijklmnopqrstuvwxyz';
        const numberChars = '0123456789';
        const symbolChars = '!@#$%^&*()_+[]{}|;:,.<>?';

        let allChars = '';
        if (includeUppercase) {
            allChars += uppercaseChars;
        }
        if (includeLowercase) {
            allChars += lowercaseChars;
        }
        if (includeNumbers) {
            allChars += numberChars;
        }
        if (includeSymbols) {
            allChars += symbolChars;
        }

        let newPassword = '';
        for (let i = 0; i < length; i++) {
            const randomIndex = Math.floor(Math.random() * allChars.length);
            newPassword += allChars[randomIndex];
        }

        setPassword(newPassword);
    };

    return (
        <div className="container">
            <form>
                <label>
                    Longueur du mot de passe: {length}
                    <input
                        type="range"
                        min="6"
                        max="20"
                        value={length}
                        onChange={(e) => setLength(e.target.value)}
                    />
                </label>
                <label>
                    <input
                        type="checkbox"
                        checked={includeUppercase}
                        onChange={() => setIncludeUppercase(!includeUppercase)}
                    />
                    Inclure des lettres majuscules
                </label>
                <label>
                    <input
                        type="checkbox"
                        checked={includeLowercase}
                        onChange={() => setIncludeLowercase(!includeLowercase)}
                    />
                    Inclure des lettres minuscules
                </label>
                <label>
                    <input
                        type="checkbox"
                        checked={includeNumbers}
                        onChange={() => setIncludeNumbers(!includeNumbers)}
                    />
                    Inclure des chiffres
                </label>
                <label>
                    <input
                        type="checkbox"
                        checked={includeSymbols}
                        onChange={() => setIncludeSymbols(!includeSymbols)}
                    />
                    Inclure des caractères spéciaux
                </label>
                <button type="button" onClick={generatePassword}>
                    Générer le mot de passe
                </button>
            </form>
            <div className="password-display">
                <strong>Mot de passe généré:</strong> {password}
            </div>
        </div>
    );
}

export default Generator;
